import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [restaurantData, setRestaurantData] = useState(null);
  const [inputValue, setInputValue] = useState("");
  const [loading, setLoading] = useState(false);

  // Use relative URL since both frontend and backend are on the same local domain
  const apiUrl = '';  // Leave it empty for relative path

  useEffect(() => {
    async function fetchRestaurantData() {
      try {
        setLoading(true);
        // Using relative path
        const response = await axios.get(`/restaurant/recommendations?query=thai`);
        setRestaurantData(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        setLoading(false);
      }
    }

    fetchRestaurantData();
  }, []);

  const formatData = (data) => {
    if (!data) {
      return "No data found";
    }
    return Object.keys(data).map((key) => (
        <div key={key}>
          <strong>{parseInt(key) + 1}:<span>   </span>
            {typeof data[key] === 'object' ? data[key].name : data[key]}</strong>

          <br/> <strong> address: </strong>
          {typeof data[key] === 'object' ? data[key].address : data[key]}

          <br/> <strong> rating: </strong>
          {typeof data[key] === 'object' ? data[key].stars : data[key]}
        </div>
    ));
  }

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!inputValue) {
      alert("Please enter some input");
      return;
    }

    try {
      // Using relative path for the request to recommendations
      const response = await axios.get('/restaurant/recommendations', {
        params: { query: inputValue } // query will be passed as params
      });
      console.log(response);

      console.log("Data submitted successfully:", response.data);
      setRestaurantData(response.data);
    } catch (error) {
      console.error('Error submitting data:', error);
    }
  };

  return (
    <div className="App">
      <h1>What food would you like to eat?</h1>

      <form onSubmit={handleSubmit}>
        <label htmlFor="inputBox">Enter cuisine:</label>
        <input
          id="inputBox"
          type="text"
          value={inputValue}
          onChange={handleInputChange}
          placeholder="thai, mexican,.."
        />
        <button type="submit">Submit</button>
      </form>

      {loading ? (
        <p>Loading restaurant data...</p>
      ) : (
        restaurantData && (
          <div>
            <h2>Our Recommendations:</h2>
            <div className="pretty-json">
              {formatData(restaurantData)}
            </div>
          </div>
        )
      )}
    </div>
  );
}

export default App;
